<?phpsession_start();define ('DB', 'mytext.txt');if (!empty($_POST['submit'])) {$name = htmlspecialchars(trim($_POST['name']));$text = htmlspecialchars(trim($_POST['txt']));$str = $name.'||'.$text."\r\n";if(file_put_contents(DB, $str, FILE_APPEND)) {$_SESSION['res'] = 'Добавился!';header("Location: index.php");exit;}}echo @$_SESSION['res'];unset($_SESSION['res']);session_destroy();?><form method = "post">Имя: <br /><p><input type = "text" name = "name" /></p>Сообщение<p><textarea cols = "30" rows = "5" name = "txt"></textarea></p><p><input type = "submit" name = "submit" value = "Добавить"/></p></form><?if(!file_exists(DB)) exit;function arr($arr) {echo '<pre>';print_r($arr);echo '</pre>';}$read = file(DB);foreach($read as $item) {$mas[] = explode('||', $item);}print_r($mas);$count = count($mas);for($i = 0; $i < $count; $i++) {echo '<div style="border: 1px solid black;/>';echo "<p>".$mas[$i][0]."</p>";echo "<p>".$mas[$i][1]."</p>";echo '</div>';}?>